(function(seajs, seacss, cssloader){
    // set base path
    var base = "/cms/resource/";

    var v = new Date().getTime();

    // To set js files
    seajs.config({
        base: base,
        alias: '{seajs_alias}',,
        preload: ["common"],
        map: [
            ['.js', '.js?v=' + v]
        ],
        debug: false
    });

   
    seacss.config({
        base: base,
        alias: '{seacss_alias}',
        debug: true,
        map: [
            ['.css', '.css?v=' + v]
        ]
    })
})(seajs, seacss, cssloader);

