#!/bin/bash
NODE=$(eval whereis node)
NODE=${NODE:6}

# read config
if [ -n "$1" ];then
	source $1
else
	echo "Required config file!"
fi

# run one time when begin this shell
eval $NODE $init $conf

inotifywait -mre modify,create,delete,move $src | while read DIR EVENT FILE 
do
	eval $NODE $exec $conf $DIR $EVENT $FILE
done
