(function(seajs, seacss, cssloader){
    // set base path
    var base = "/cms/resource/";

    var v = new Date().getTime();

    // To set js files
    seajs.config({
        base: base,
        alias: {
            "jquery": "lib/cmd/jquery/2.0.3/jquery.js",
            "jquery.form": "lib/cmd/jquery/form/3.50.0/jquery.form.js",
            "jquery-validate": "lib/cmd/jquery-validate/1.13.0/jquery.validate.js",
            "select2": "lib/cmd/select2/3.5.1/select2.js",
            "bootstrap": "lib/cmd/bootstrap/3.2.0/js/bootstrap.min.js",
            "bootstrap-table": "lib/cmd/bootstrap/table/1.2.3/bootstrap-table.min.js",
            
            "swfupload": "lib/cmd/swf/swfupload.js",
            "jqueryui": "lib/cmd/jquery/jqueryui/1.11.0/jquery-ui.min.js",
            "validation": "lib/cmd/jquery/validation/1.10.0/jquery.validate.min.js",
            "pagingbar": "page/app/common/js/common.pagingbar.js",

            "common": "page/app/common/js/common.js",

            "ivy.alert": "page/widget/ivy_alert/js/ivy.alert.js",
            "ivy.alert.css": "page/widget/ivy_alert/css/ivy.alert.css",
            "tpl.alert": "page/widget/ivy_alert/template/tpl.ivy.alert.js",

            "ipick.fc.langname": 'page/widget/form-control-langname/fc.langname.js',
            "ipick.file-upload": "page/widget/file-upload/js/file-upload.js",
            "ipick.home-preview": "page/widget/home-preview/js/home-preview.js",
            "ipick.search-preview": "page/widget/search-preview/js/search-preview.js",
            "ipick.banner-preview": "page/widget/banner-preview/js/banner-preview.js",
            "ipick.foodie-preview": "page/widget/foodie-preview/js/foodie-preview.js",
            "ipick.data-table": "page/widget/data-table/js/data-table.js",
            "ipick.advanced-pagination": "page/widget/advanced-pagination/js/advanced-pagination.js",

            // seajs plugins
            "seajs-style": "js/lib/seajs/plugins/seajs-style/seajs-style.js",
            "seajs-log": "js/lib/seajs/plugins/seajs-log/seajs-log.js",
            "seajs-debug": "js/lib/seajs/plugins/seajs-debug/seajs-debug.js"
        },
        preload: ["common"],
        map: [
            ['.js', '.js?v=' + v]
        ],
        debug: false
    });

    // To load css files
    cssloader.config({
        base: base,
        alias: {
            "reset" :   "page/app/common/css/reset.css",
            "common" :   "page/app/common/css/common.css",
            "common.pagingbar" :   "page/app/common/css/common.pagingbar.css",

            "bootstrap": "lib/cmd/bootstrap/3.2.0/css/bootstrap.min.css",
            "select2": "lib/cmd/select2/3.5.1/select2.css",
            "select2-bootstrap": "lib/cmd/select2/3.5.1/select2-bootstrap.css"
        },
        release: false,
        autoLoad: [ "bootstrap"],
        map: [
            ['.css', '.css?v=' + v]
        ]
    });

    seacss.config({
        base: base,
        alias: {
            "reset" :   "page/app/common/css/reset.css",
            "common" :   "page/app/common/css/common.css",
            "common.pagingbar" :   "page/app/common/css/common.pagingbar.css",

            "bootstrap": "lib/cmd/bootstrap/3.2.0/css/bootstrap.min.css",
            "select2": "lib/cmd/select2/3.5.1/select2.css",
            "select2-bootstrap": "lib/cmd/select2/3.5.1/select2-bootstrap.css"
        },
        debug: true,
        map: [
            ['.css', '.css?v=' + v]
        ]
    })
})(seajs, seacss, cssloader);

