
define(function (require, exports, module) {

	var $ = require('jquery');

	// Switch language
	$("#lang").change(function (event) {
		var lang = $(this).val();
		var url = window.location.href;
		var baseUrl = url.split('?')[0];
		var queryString = $.grep((url.split('?')[1] || '').split('&'), function (param, index) {
			return param && !param.match(/^lang=/);
		});
		queryString.push('lang=' + lang);
		url = baseUrl + '?' + queryString.join('&');
		window.location.replace(url);
	});

});