
define(function (require, exports, module) {

	var $ = require('jquery');
	require('jquery-validate');
	require('jquery.form');
	var IVYAlert = require('ivy.alert');

	require('ipick.file-upload');

	// Upload Icon
	$('#icon-upload').fileUpload({
		url: '/cms/index.php/upload/up2tfs',
		/*params: {
			'width': 110,
			'height': 110,
			'check_size': true
		},*/
		file: {
			types: '*.jpg;*.png;*.gif',
			typesDescription: 'JPG/PNG/GIF Images',
			sizeLimit: '2 MB',
		},
	}).on('uploadsuccess', function (event) {
		var data = $.parseJSON(event.body);
		if (data['ret']) {
			IVYAlert(data['msg']);
		} else {
			$('#icon').val(data['data']['pic_url']);
			$('#uploaded-icon').removeClass('hidden').attr('src', data['data']['pic_url']);
			IVYAlert('Icon uploaded successfully');
		}
	}).on('uploadstart', function (event) {
		IVYAlert('Uploading...', { toggle: true });
	}).on('error', function (event) {
		var message = {
			'QUEUE_ERROR.QUEUE_LIMIT_EXCEEDED': 'You can only upload one file at a time',
			'QUEUE_ERROR.ZERO_BYTE_FILE': 'The file is empty',
			'QUEUE_ERROR.FILE_EXCEEDS_SIZE_LIMIT': 'The file exceeded the maximum file size (2 MB)',
			'QUEUE_ERROR.INVALID_FILETYPE': 'The file format should be .bmp, .png, .jpeg, .jpg or .gif'
		}[event.error.type] || event.error.message;
		IVYAlert(message);
	});

	// Upload Articles TSV File
	$('#articles-tsv-upload').fileUpload({
		url: '/cms/index.php/upload/do_upload',
		file: {
			types: '*.tsv',
			typesDescription: 'TSV Files',
			sizeLimit: '2 MB',
		},
	}).on('uploadsuccess', function (event) {
		var data = $.parseJSON(event.body);
		if (data['ret']) {
			IVYAlert(data['msg']);
		} else {
			$('#articles-tsv').val(data['data']['upload_path']);
			$('#articles-tsv-name').val('File Name: ' + data['data']['upload_path'].replace(/^.*\//, ''));
			IVYAlert('TSV file uploaded successfully');
		}
	}).on('uploadstart', function (event) {
		IVYAlert('Uploading...', { toggle: true });
	}).on('error', function (event) {
		var message = {
			'QUEUE_ERROR.QUEUE_LIMIT_EXCEEDED': 'You can only upload one file at a time',
			'QUEUE_ERROR.ZERO_BYTE_FILE': 'The file is empty',
			'QUEUE_ERROR.FILE_EXCEEDS_SIZE_LIMIT': 'The file exceeded the maximum file size (2 MB)',
			'QUEUE_ERROR.INVALID_FILETYPE': 'The file format should be .tsv'
		}[event.error.type] || event.error.message;
		IVYAlert(message);
	});

	// Upload Manually TSV File
	$('#manually-tsv-upload').fileUpload({
		url: '/cms/index.php/upload/do_upload',
		file: {
			types: '*.tsv',
			typesDescription: 'TSV Files',
			sizeLimit: '2 MB',
		},
	}).on('uploadsuccess', function (event) {
		var data = $.parseJSON(event.body);
		if (data['ret']) {
			IVYAlert(data['msg']);
		} else {
			$('#manually-tsv').val(data['data']['upload_path']);
			$('#manually-tsv-name').val('File Name: ' + data['data']['upload_path'].replace(/^.*\//, ''));
			IVYAlert('TSV file uploaded successfully');
		}
	}).on('uploadstart', function (event) {
		IVYAlert('Uploading...', { toggle: true });
	}).on('error', function (event) {
		var message = {
			'QUEUE_ERROR.QUEUE_LIMIT_EXCEEDED': 'You can only upload one file at a time',
			'QUEUE_ERROR.ZERO_BYTE_FILE': 'The file is empty',
			'QUEUE_ERROR.FILE_EXCEEDS_SIZE_LIMIT': 'The file exceeded the maximum file size (2 MB)',
			'QUEUE_ERROR.INVALID_FILETYPE': 'The file format should be .tsv'
		}[event.error.type] || event.error.message;
		IVYAlert(message);
	});

	// Switch Type
	$('#type').change(function (event) {
		var activePanel = {
			'1': $('#type-tags-panel'),
			'3': $('#type-articles-panel'),
			'2': $('#type-manually-panel')
		}[$(this).val()];
		$('.form-panel').addClass('hidden');
		if (activePanel) activePanel.removeClass('hidden');
	}).change();

	// Validation
	$('#form').validate({
		errorPlacement: function (error, element) {
		}
	});

	// Ajax Submit
	$('form')
		.ajaxForm({
			beforeSerialize: function (data) {
				if(!$('#icon').val()){
					IVYAlert('You should upload an icon before submit');
					return false;
				}
				if ($('#type').val() === '1') {
					$('#sort').val(JSON.stringify({
						'sort_by': $('#sort-key').val(),
						'sort_type': $('#sort-order').val()
					}));
				}
				if ($('#type').val() === '3') {
					if(!$('#articles-tsv').val()){
						IVYAlert('You should upload an TSV file before submit');
						return false;
					}
					$('#tsv').val($('#articles-tsv').val());
				}
				if ($('#type').val() === '2') {
					if(!$('#manually-tsv').val()){
						IVYAlert('You should upload an TSV file before submit');
						return false;
					}
					$('#tsv').val($('#manually-tsv').val());
				}
			},
			beforeSubmit: function (data) {
				IVYAlert('Submitting...', { toggle: true });
			},
			success: function (data) {
				data = $.parseJSON(data);
				if (data['ret']) {
					IVYAlert(data['msg']);
				} else {
					IVYAlert('Your edit has been submitted', function () {
						window.location = '../home_search/index';
					});
				}
			}
		});

});