    $('#restaurant-register-form').validate({
        rules: {
            // desc: 'required'
        },
        errorElement: 'span',
        errorPlacement: function(error,el){
        },
        submitHandler:function(form) {
            IVYAlert("Loading...", {
                toggle: true
            });
            var data = {
                id: form.id.value,
                type: $('#status > label.active > input').val(),
                weight: $('#weight').val(),
                editorial_weight: $('#editorial_weight').val(),
                description: form.desc.value
            };

            if(!data.type){
                IVYAlert('Need review status');
                return;
            }

            $.post('/cms/index.php/rest_verify/approval', data, function(rs){
                if(rs.ret == 0){
                    IVYAlert(rs.msg, function(){
                        location.href = "/cms/index.php/rest_verify/index";
                    });
                }else{
                    IVYAlert(rs.msg);
                }
            },'json');
        }
    });

	
    function getI18nJSON(selector){
        var obj = {};
        $(selector).parent().find('input, textarea').each(function(i, e){     
            var key = $(e).data('lang');
            var val = $(e).val();
            obj[key] = val;
        })
        return JSON.stringify(obj);
    }

    function encodeTagsData(data) {
        return $(data || []).map(function (index, tag) {
            return {
                id: JSON.stringify(
                    {id: tag['id'], name: tag['name']}),
                text: tag['name']
            };
        });
    };
    function decodeTagsData(string) {
        return $(string.split('##')).map(function (index, tagString) {
            return tagString ? JSON.parse(tagString) : '[]';
        });
    };
	
