
define(function (require, exports, module) {

	var $ = require('jquery');
	require('bootstrap');
	require('../css/home-preview.css');

	$.fn.extend({

		homePreview: function (options) {

			options = options || {};

			this.each(function (index, element) {
				
				if (!$(this).data('homePreview')) {
					$(this).data('homePreview', {});
					
					var items = $(this).find('li').map(function (index, element) {
						return {
							title: $(this).attr('data-title'),
							iconUrl: $(this).attr('data-icon-url')
						};
					});
					
					$(this)
						.addClass('home-preview')
						.empty()
						.append($('<div class="preview"></div>')
							.append($('<ul class="clearfix"></ul>'))
						)
						.homePreview().addItem(items);
				}
			});

			return {

				$this: this,

				clear: function () {

					this.$this.find('.preview > ul')
						.empty();

					return this.$this;
				},

				addItem: function (item) {

					var container = this.$this;

					$(item).each(function (index, item) {
						container.find('.preview > ul')
							.append($('<li class="pull-left"></li>')
								.data('homePreview', {
									title: item.title,
									iconUrl: item.iconUrl
								})
								.append($('<img src="' + item.iconUrl + '" alt="' + item.title + '" />'))
								.append($('<p></p>')
									.html(item.title)
								)
							);
					});

					return this.$this;
				},

				getItemList: function () {

					return this.$this.find('.preview > ul > li').map(function (index, element) {
						return {
							title: $(this).data('homePreview').title,
							iconUrl: $(this).data('homePreview').iconUrl
						};
					}).get();
				}

			};
		}

	});

});