/**
 * @author
 * @desc
 */
define(function(require, exports, module){
    /**
     * @param
     * @desc
     */
    function formatTime() {
        var date;
        var pattern;
        var Y;
        var M;
        var D;
        var h;
        var m;
        var s;
        if(arguments.length == 1){
            // As format string, get current time
            date = new Date();
            pattern = arguments[0];
        }else{
            // The first argument is input date
            date = new Date(arguments[0]);
            pattern = arguments[1];
        }
        Y = date.getFullYear();
        M = date.getMonth() + 1;
        D = date.getDate();
        h = date.getHours();
        m = date.getMinutes();
        s = date.getSeconds();

        pattern = pattern.replace('Y', Y).replace('M', m).replace('D', D).replace('h', h).replace('m', m).replace('s', s);

        return pattern;
    }

    module.exports = {
        formatTime: formatTime
    };
})
