define("page/widget/banner-preview/css/banner-preview.css",[],function(){seajs.importStyle(".banner-preview{position:relative;margin:-15px}.banner-preview .switch-btn{position:absolute;z-index:1010;right:0;top:0;width:50px;height:50px;padding:0;padding-left:5px;border:0;border-right:1px solid rgba(85,85,85,.85);border-bottom:1px solid rgba(85,85,85,.85);border-bottom-left-radius:15px;font-size:16px;color:#fff;background:rgba(68,68,68,.85);opacity:0;outline:0;-webkit-transition:opacity .15s;-o-transition:opacity .15s;transition:opacity .15s}.banner-preview:hover .switch-btn{opacity:1}.banner-preview .preview{width:320px;height:500px;border-bottom-left-radius:3px;border-bottom-right-radius:3px;overflow:hidden}.banner-preview .preview.carousel-preview{background:#f0f2f1 url(../img/bg.jpg) center -15px no-repeat;background-size:100%}.banner-preview .preview.carousel-preview .search-banner{position:relative;margin:104px auto 0;width:280px;height:85px;overflow:hidden}.banner-preview .preview.carousel-preview .search-banner>ul{position:absolute;left:0;top:0;width:9999px;margin:0;padding:0;list-style:none;-webkit-transition:left .5s;-o-transition:left .5s;transition:left .5s}.banner-preview .preview.carousel-preview .search-banner>ul>li{width:280px;height:85px;background:#ddd url(http://htpic.tc.qq.com/IBG_c5a7661f7241f698463bd41698c33ddc) center center no-repeat;background-size:100%}.banner-preview .preview.carousel-preview .search-banner>ul>li img{width:100%}.banner-preview .preview.carousel-preview .search-banner>.search-banner-btn{position:absolute;top:0;z-index:1005;width:50px;height:100%;padding:0 10px;border:0;font-size:20px;color:#fff;text-shadow:0 1px 2px rgba(0,0,0,.6);background:transparent;opacity:0;outline:0;-webkit-transition:opacity .15s;-o-transition:opacity .15s;transition:opacity .15s}.banner-preview .preview.carousel-preview .search-banner:hover>.search-banner-btn{opacity:.8}.banner-preview .preview.carousel-preview .search-banner>.search-banner-btn:hover{opacity:1}.banner-preview .preview.carousel-preview .search-banner>.prev-btn{left:0;text-align:left}.banner-preview .preview.carousel-preview .search-banner>.next-btn{right:0;text-align:right}.banner-preview .preview.list-preview{background:#f0f2f1;overflow-x:hidden;overflow-y:auto}.banner-preview .preview.list-preview>ul{width:280px;margin:0 auto;padding:0;list-style:none}.banner-preview .preview.list-preview>ul>li{height:85px;margin:12px 0;overflow:hidden}.banner-preview .preview.list-preview>ul>li img{width:100%;cursor:default}")});