/**
 * ipick alert
 */
function ipickDatepicker(){
    this.setting = {
        txt: {
            year: "YEAR"
        }
    }

    this.tplWrap = '<div class="ipick-datepicker-wrap">\
            <div class="ipick-datepicker-main">\
                <div class="ipick-datepicker-head">\
                    <div class="ipick-datepicker" data-rel="ipick-datepicker-head"></div>\
                </div>\
                <div class="ipick-datepicker" data-rel="ipick-datepicker-content"></div>\
                <div class="ipick-datepicker-foot">\
                    <a href="javascript:void(0);" data-rel="cancel">{txt_cancel}</a>\
                    <a href="javascript:void(0);" data-rel="set">{txt_set}</a>\
                </div>\
            </div>\
        </div>';

    this.tplTitleItem = '<div class="ipick-datepicker-title">{title}</div>';

    this.tplColItem = '<div class="ipick-datepicker-ulw">\
                        <div class="ipick-datepicker-mark"></div>\
                        <div class="ipick-datepicker-ulm" data-rel="ipick-datepicker-ulm">\
                            <ul class="ipick-datepicker-ul"></ul>\
                        </div>\
                    </div>';
}


/**
 * 配置默认参数
 * @param   {object}  data
 * data主要有setting属性，设定确认框的取消、确定的wording
 * data = {
 *     setting: {
 *      cancel: "取消"
 *      ok: "确定"
 *     }
 * }
 */
ipickDatepicker.prototype.config = function(data){
    if(!data){
        return;
    }
    $.extend(this.setting, data.setting);
}


/**
 * 确认框框主要调用接口
 * @param   {string}    msg     文本格式，不支持html
 * @param   {function}  callback    点击确定的回调函数
 */
