var fn = require('synchronize-files');

var exceptsrc 	= '';
var exceptdist 	= '';
var onlysrc 	= '';
var onlydist 	= '';

circle(function(){
	
})

function circle(callback, delay, limit){
    if(typeof callback !== 'function'){
        return;
    }

    // exec per 2 seconds
    delay ? null : delay = 1500;

    // exec total time
    limit ? null : limit = 60000;

    var s = new Date().getTime();
	var e;
    var i = setInterval(function(){
        callback();
		e = new Date().getTime();
        if(e - s > limit){
            clearInterval(i);
            console.log('Total:' + (e-s));
        }
    }, delay)
}