var FS = require('fs');
var PATH = require('path');


module.exports = mkdir;


/**
* @param	{string}	dir		path
* @desc		like shell command mkdir
*/
function mkdir(dir){
	if(!FS.existsSync(dir)){
		var _path = dir.split(/\/|\\/);
		if(_path.length === 1){
			return;
		}
		_path.pop();
		mkdir(_path.join('/'));
		FS.mkdirSync(dir);
	}
}