var FS = require('fs');
var PATH = require('path');


module.exports = rm;


/**
* @param	{string}	path		remove src
* @desc		like shell command rm
*/
function rm(path){
	if(FS.statSync(path).isDirectory()){
		var ll = FS.readdirSync(path);
		ll.forEach(function(name){
			rm(PATH.join(path, name));
		})
		FS.rmdirSync(path);
	}else{
		FS.unlinkSync(path);
	}
}